import { useState } from "react";

function ReservationForm() {
  const [formData, setFormData] = useState({
    date: "",
    time: "10:00",
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("予約内容:", formData);
    alert("送信しました！");
  };

  return (
    <div className="min-h-screen bg-stone-50 flex flex-col items-center justify-center px-4">
      <h1 className="text-2xl font-semibold mb-6">予約</h1>
      <form
        onSubmit={handleSubmit}
        className="bg-white shadow-md rounded-lg p-6 w-full max-w-md space-y-4"
      >
        <label className="block">
          <span className="block mb-1 text-sm font-medium">日付</span>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </label>

        <label className="block">
          <span className="block mb-1 text-sm font-medium">時間</span>
          <select
            name="time"
            value={formData.time}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          >
            <option value="10:00">10:00</option>
            <option value="13:00">13:00</option>
            <option value="15:00">15:00</option>
          </select>
        </label>

        <label className="block">
          <span className="block mb-1 text-sm font-medium">お名前</span>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </label>

        <label className="block">
          <span className="block mb-1 text-sm font-medium">メールアドレス</span>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </label>

        <label className="block">
          <span className="block mb-1 text-sm font-medium">予約内容</span>
          <textarea
            name="message"
            rows="3"
            value={formData.message}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
        </label>

        <button
          type="submit"
          className="w-full bg-rose-300 hover:bg-rose-400 text-white py-2 rounded font-medium transition"
        >
          予約する
        </button>
      </form>
    </div>
  );
}

export default ReservationForm;
